INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1005, 1, 'Arun', 'Gupta', 1, 'Arun Gupta is a Java evangelist working at Oracle. He works to create and foster the community around Java EE, GlassFish, and WebLogic. He has been with the Java EE team since its inception and contributed to all releases. Arun has extensive world wide...');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1006, 1, 'Lincoln', 'Baxter III', 1, 'Lincoln Baxter, III is a Research Engineer at Red Hat, working on JBoss open-source projects; most notably as project lead for JBoss Forge.');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1007, 1, 'Ed', 'Burns', 1, 'Ed Burns is a Senior Staff Engineer for Oracle Corporation where he chairs the JSR 344 (JSF 2.2) Expert Group and contributes to the open source Mojarra project. He is the author of two books published by McGraw-Hill: Secrets of the Rock Star Programme...');
