INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1009, 1, 'Antonio', 'Goncalves', 0, 'Antonio is a senior developer specialized in Java/Java EE. As a consultant he advises customers and helps them in defining and developing their software architecture. This Java Champion is also the founder of the very successful Paris JUG, Devoxx Franc...');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1010, 1, 'Adam', 'Bien', 6, 'Consultant and author Adam Bien is an Expert Group member for the Java EE 6 / 7, EJB 3.X, JAX-RS and JPA 2.X JSRs. He has worked with Java technology since JDK 1.0 and with Servlets/EJB 1.0 and is now an architect and developer for Java SE and Java EE ...');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1011, 1, 'Emmanuel', 'Bernard', 0, 'Emmanuel Bernard is data platform architect at JBoss by Red Hat where he oversees data projects of the Red Hat''s middleware and is member of the Hibernate team. Emmanuel has spent a few years in the retail industry as developer and architect where he ...');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1012, 1, 'Jitendra', 'Kotamraju', 1, 'Jitendra Kotamraju is a software engineer at Oracle. He has been contributing to the many Java EE technologies and GlassFish projects for the last 8 years. He was the specification and implementation lead of JAX-WS 2.2. At present, he is the specificat...');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1013, 1, 'Mike', 'Keith', 1, 'Mike Keith has been an enterprise, distributed systems and persistence expert for over 20 years. He is a Java and Enterprise Architect at Oracle and contributes to Java EE and many of the subspecifications that make up the enterprise Java portfolio. He...');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1014, 1, 'Anthony', 'Lai', 1, 'Anthony Lai is a principal member of technical staff at Oracle. He is the specification lead for JSR 236 - Concurrency Utilities for Java EE 1.0, and was previously a member of the J2EE Connector Architecture 1.5 specification (JSR 112) expert group.');
INSERT INTO AUTHOR (id, version, first_name, last_name, preferred_language, bio) VALUES (1015, 1, 'J. K.', 'Rowling', 1, 'J K (Joanne Kathleen) Rowling was born in the summer of 1965 at Yate General Hospital in England and grew up in Chepstow, Gwent where she went to Wyedean Comprehensive. Jo left Chepstow for Exeter University, where she earned a French and Classics degree, and where her course included one year in Paris');
INSERT INTO ITEM (id, version, discriminator, isbn, title, rank, small_image_url, medium_image_url, language, unit_cost, nb_of_pages, publication_date, category_id, publisher_id, description) VALUES ( 1001, 1, 'B', '1931182310', 'Advanced Java EE Development for Rational Application Developer 7.5: Developers'' Guidebook', 2, 'http://ecx.images-amazon.com/images/I/51bjnhlGbeL._SL75_.jpg', 'http://ecx.images-amazon.com/images/I/51bjnhlGbeL._SL160_.jpg', 2, 79.95, 752, parsedatetime('2011-11-21', 'YYYY-MM-DD'), 1000, 1008, 'Written by IBM senior field engineers and senior product development experts, this advanced book provides a solid look at the development of a range of core Java EE technologies, as well as an in-depth description of the development facilities provided by IBM Rational Application Developer version 7.5. Since the Java EE developmental platform incorporates a wide range of technologies from disparate and myriad sources, this up-to-date guidebook helps developers triumph over the complexity and depth of knowledge required to build successful architectures. Senior developers, engineers, and architects—especially those who work with Rational Application Developer and those seeking certification at the Sun-certified Java master-tier level or the IBM Rational Application Developer certified professional and certified advanced professional levels—will appreciate this convenient, single reference point.');
