INSERT INTO ITEM (id, version, discriminator, isbn, title, rank, small_image_url, medium_image_url, language, unit_cost, nb_of_pages, publication_date, category_id, publisher_id, description) VALUES ( 1021, 1, 'B', '1430246928', 'Beginning EJB 3, Java EE, 7th Edition', 1, 'http://ecx.images-amazon.com/images/I/51bamFsgY9L._SL75_.jpg', 'http://ecx.images-amazon.com/images/I/51bamFsgY9L._SL160_.jpg', 6, 49.99, 452, parsedatetime('2013-05-08', 'YYYY-MM-DD'), 1008, 1010, 'Targeted at Java and Java EE developers both with and without prior Enterprise JavaBeans (EJB) experience, Beginning EJB 3, Java EE 7 Edition will guide you in building enterprise software based on the latest cross-platform industry standards. Learn how to use the EJB 3 API to develop powerful, standards-based backend business logic for creating the complex enterprise applications that run today''s transactions and more. With over 20 years of combined EJB experience, the authors offer many practical insights into designing and coding EJB components in combination with related Java EE technologies. EJB has made huge advances in the ease of building enterprise, server-side Java components and business logic development, and has drastically simplified programming models and design patterns. Use this book to harness that power and take your enterprise Java development to the next level.');
