INSERT INTO ITEM (id, version, discriminator, title, nb_of_discs, rank, small_image_url, medium_image_url, language, unit_cost, publication_date, genre_id, label_id, description) VALUES ( 1170, 1, 'C', 'Ten', 1, 1, 'http://ecx.images-amazon.com/images/I/61h76oemBPL._SL75_.jpg', 'http://ecx.images-amazon.com/images/I/61h76oemBPL._SL160_.jpg', 4, 16.99, null, 1002, 1002, 'ZOHO debut release of Peruvian trumpeter Gabriel Alegría and his colorful sextet of New York-based Latin and Peruvian musicians, with celebrity guests Ron Carter, Arturo O Farrill, and Badal Roy. Celebrates the band s 10-year anniversary with a unique mix of classic U.S. and Peruvian songs and hymns, including stunning versions both countries National Anthems! An insistently inventive program of American and Peruvian standards transformed by Alegría s highly personal synthesis of folkloric Afro-Peruvian rhythms, jazz, and other musical strains.');
